import React, { useState } from "react";
import mc from "./battlefield.module.scss";
import rock from "../../../img/rock.svg";
import paper from "../../../img/paper.svg";
import scissors from "../../../img/scissors.svg";

import rockIa from "../../../img/rock_ia.svg";

const Battlefield = () => {
  const [playerHadChoose, setPlayerHadChoose] = useState(false);
  return (
    <div className={mc.container}>
      <div className={mc.fightingZone}>
        <div className={`adaptive-img-contain ${mc.playerZone}`}>
          <span>
            <img src={rock} alt="" />
          </span>
        </div>
        <div className={`adaptive-img-contain ${mc.iaZone}`}>
          <span>
            <img src={rockIa} alt="" />
          </span>
        </div>
      </div>
      <div className={mc.rules}>
        <p>Fait ton choix </p>
      </div>
      <div className={mc.choosingZone}>
        <div className={`adaptive-img-contain ${mc.rock}`}>
          <span>
            <img src={rock} alt="" />
          </span>
        </div>
        <div className={`adaptive-img-contain ${mc.paper}`}>
          <span>
            <img src={paper} alt="" />
          </span>
        </div>
        <div className={`adaptive-img-contain ${mc.scissors}`}>
          <span>
            <img src={scissors} alt="" />
          </span>
        </div>
      </div>
    </div>
  );
};

export default Battlefield;
